mapper.select:
select no,title,mem_id,writeDate,hit,levNo
from(
	select rownum rnum,no,title,mem_id,writeDate,hit,levNo
	from(
	select no,title,mem_id,writeDate,hit,levNo
	from qboard
	order by refNo desc,ordNo
	)
	)where rnum between #{startRow} and #{endRow}

mapper.insert:
INSERT INTO SCMS_BBS
  ( 	NTT_SJ
		, NTT_CN
		, NTT_NO
		, PARENT_NO
		, NOTICE_YN
		, ATCH_FILE_ID
		, TEMP_COL_1
		, TEMP_COL_2
		, DEL_YN
		, CRT_ID
		, CRT_DT
		, CRT_IP_ADDR
	)
VALUES
    (
        #{nttSj}
	  , #{nttCn}
	  , #{nttNo}
	  , #{parentNo}
	  , #{noticeYn}
	  , #{AtchFileId}
	  , #{tempCol1}
	  , #{tempCol2}
	  ,'N'
	  ,	#{crtId}
	  , NOW()
	  , #{crtIpAddr}
	)
